package week1.day1;

public class LearnSwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String wkend = "Sun";
		
		switch (wkend) {
		case "mon":
			System.out.println("mon");
			
			break;
		case "Tues":
			System.out.println("Tues");
				
		break;
			
		case "Wed":
			System.out.println("Wed");
					
		break;
				
		case "Sun":
			System.out.println("Sun");
						
			break;
		
		default:
			System.out.println("sun");
			break;
		}

	}
}

